mapper.select:
SELECT count(1) FROM MEMBER_TB WHERE 1 = 1
and date_format(MEMBER_TB.ins_dt, '%Y%m%d') between date_format(#{item.startDate}, '%Y%m%d') and date_format(#{item.endDate}, '%Y%m%d')
AND DEPT_SEQ = #{item.searchWord}
AND WORK_STATE = #{item.searchWord}
AND AUTH_CODE = #{item.searchWord}
AND REV_YN = #{item.searchWord}
and id like concat('%',#{item.searchWord},'%')
and mem_name like concat('%',#{item.searchWord},'%')
AND FN_POS_NAME (id) like concat('%',#{item.searchWord},'%')
AND PHONE like concat('%',replace(#{item.searchWord},'-',''),'%')

set @rownum:=0;
SELECT lst.* FROM ( SELECT @rownum:=@rownum+1 as no, LPAD (member_seq, 4, 0) memberSeq, IFNULL (date_format (MEMBER_TB.IN_DT, '%Y-%m-%d'), '') inDt, FN_DEPT_NAME (id) deptName, id, mem_name, ifnull(eng_name, '') eng_name, FN_POS_NAME (id) posName, REV_YN, EMAIL, IFNULL (FN_GET_PHONE_FORMAT (PHONE), '') PHONE, IFNULL (AUTH_CODE, '') AUTH_CODE, CONCAT ( FN_CODE_NAME (WORK_STATE, 'PW'), IFNULL ( CASE WHEN WORK_STATE = 2 THEN CONCAT ( ' (', IFNULL ( date_format (MEMBER_TB.LEAVE_S_DT, '%Y-%m-%d'), '' ), '~', IFNULL ( date_format (MEMBER_TB.LEAVE_E_DT, '%Y-%m-%d'), '' ), ')' ) WHEN WORK_STATE = 3 THEN CONCAT ( '(', IFNULL (date_format (MEMBER_TB.QUIT_DT, '%Y-%m-%d'), ''), ')' ) END, '' ) ) workSate, date_format (MEMBER_TB.LEAVE_S_DT, '%Y-%m-%d') leaveSDt, date_format (MEMBER_TB.LEAVE_S_DT, '%Y-%m-%d') leaveEDt, date_format (MEMBER_TB.LEAVE_S_DT, '%Y-%m-%d') quitDt, LOCK_YN from MEMBER_TB WHERE 1 = 1
and date_format(MEMBER_TB.IN_DT, '%Y%m%d') between date_format(#{item.startDate}, '%Y%m%d') and date_format(#{item.endDate}, '%Y%m%d')
AND DEPT_SEQ = #{item.searchWord}
AND WORK_STATE = #{item.searchWord}
AND AUTH_CODE = #{item.searchWord}
AND REV_YN = #{item.searchWord}
and id like concat('%',#{item.searchWord},'%')
and mem_name like concat('%',#{item.searchWord},'%')
AND FN_POS_NAME (id) like concat('%',#{item.searchWord},'%')
AND PHONE like concat('%',replace(#{item.searchWord},'-',''),'%')
order by MEMBER_TB.IN_DT ASC ) lst ORDER BY no DESC LIMIT #{pageUnit} OFFSET #{firstIndex};


SELECT password AS password
  FROM MEMBER_TB
 WHERE ID = #{id}
   AND PASSWORD = #{password}

SELECT COUNT(1) 
FROM MEMBER_TB
WHERE ID = #{id}
