mapper.select:
SELECT
JOB_TB.SBK_YM, JOB_TB.SBK_SEQ, SBK_TB.REVISION, CONCAT ('SB', SBK_TB.SBK_YM, '-' , SBK_TB.TYPE, LPAD(SBK_TB.SBK_SEQ,4,0), IF(REVISION > 0, CONCAT ('-',REVISION), '')) sbkId , IFNULL (CONCAT (FN_GET_SEQSTRING('Q', JOB_TB.QUO_YM, 'G', JOB_TB.QUO_SEQ), IF(JOB_TB.SECRET_YN=1,'-A','')), '') quoId, QUO_TB.VERSION, IFNULL(DATE_FORMAT(SBK_TB.EST_CMP_DT, '%Y-%m-%d'), '-') estCmpDt, FN_MEM_NAME(JOB_TB.MNG_ID) MEM_NAME, /* 업무담당자 추가 */ FN_MEM_NAME(SBK_TB.INS_MEM_ID) INS_MEM, /* 작성자 추가 */
/* 업체정보 */ IFNULL ((SELECT CMPY_CODE FROM CMPY_TB WHERE CMPY_SEQ = JOB_TB.CMPY_SEQ),'') CMPY_CODE, FN_CUST_TYPE (JOB_TB.CMPY_SEQ) cmpyType, JOB_TB.CMPY_SEQ, JOB_TB.CMPY_MNG_SEQ, IFNULL (CMPY_TB.CMPY_NAME, '') cmpyTitle, IFNULL (CMPY_MNG_TB.NAME, '') cmpyMngName,
/* 업체/신청사 신버젼 */ PRTN_SEQ, PRTN_MNG_SEQ, PRTN_YN, DIRT_SEQ, DIRT_MNG_SEQ,
/* 신청인 */ JOB_TB.CMPY_NAME, SBK_TB.BSNS_RGNMB, SBK_TB.RPRSN, SBK_TB.CRPRT_RGNMB, ADDRESS, SBK_TB.RSDNT_RGNMB, INDST_MF_YN, INDST_SL_YN, INDST_IP_YN, JOB_TB.MNG_NAME, JOB_TB.MNG_EMAIL, FN_GET_PHONE_FORMAT (IFNULL(MNG_TEL, '')) MNG_TEL, FN_GET_PHONE_FORMAT (IFNULL(JOB_TB.MNG_FAX, '')) MNG_FAX, FN_GET_PHONE_FORMAT (IFNULL(JOB_TB.MNG_PHONE, '')) MNG_PHONE,
/* 신청기기 */ JOB_TB.PRDCT_NAME, JOB_TB.MODEL_NAME, EXTEND_MODEL, SBK_TB.CMPNY_IDNTF, ATHNT_NMBR, NEW_CMPNY_IDNTF1, NEW_CMPNY_IDNTF2, NEW_CMPNY_IDNTF3, ELCTR_RTNG_W, CLOCK_FRQNC, MDL_IDNTF, ADD_DEV,
/* 제조자 */ MNFCT_CMPNY, MNFCT_CNTRY_CODE, IFNULL (FN_CODE_NAME (MNFCT_CNTRY_CODE, 'SN'), '') MNFCT_CNTRY, MNFCT_ADRES, ADD_MNFCT_CMPNY, ADD_MNFCT_CNTRY,
/* 시험 */ TEST_PLACE_IN_YN, TEST_PLACE_OUT_YN, TEST_ADRES, ELCTR_TEST_C_YN, ELCTR_TEST_K_YN, ELCTR_TEST_N_YN, TEST_RPRT_K_YN, TEST_RPRT_N_YN, TEST_RPRT_JODAL_YN, TEST_RPRT_SPRT_YN, TEST_RPRT_SPRT, TEST_RPRT_ETC_YN, TEST_RPRT_ETC,
/* 신청규격 */ SG_NEW_YN, SG_GB_YN, SG_DG_YN, SG_ETC_YN, CF_JD_YN, CF_JI_YN, CF_JG_YN, CF_JJ_YN, CF_ETC1_YN, CF_ETC1, CF_AI_YN, CF_AH_YN, CF_GJ_YN, CF_DJ_YN, CF_EH_YN, CF_ETC2_YN, CF_ETC2, CF_CD_YN, CF_CC_YN, CF_FCC_YN, CF_VCCI_YN, CF_PSE_YN, TEST_EMC_YN, TEST_EM_YN, TEST_EMF_YN, TEST_TEL_YN, TEST_ROHS_YN, TEST_SS_YN, TEST_SAF_YN, TEST_SM_YN, TEST_DJ_YN, TEST_EH_YN, TEST_RF_YN, TEST_SAR_YN, TEST_OUT_YN, TEST_IN_YN, CLASS_A_YN, CLASS_B_YN, PP_YN, PP_NAME, PP_NUM, PP_BL, PP_CNT, DOC_URL, DOC_IS_YN, DOC_SS_YN, DOC_HD_YN, DOC_BB_YN, DOC_PD_YN, DOC_Bl_YN, DOC_JB_YN, DOC_JS_YN, DOC_CR_YN, DOC_SI_YN, DOC_SG_YN, DOC_SB_YN, CNFRM_YN, IM_EUT_YN, IM_CLNT_YN, IM_DLVRY_YN, IM_DSPSL_YN, IM_ETC_YN, CUS_INFO_AGREE_YN, EST_CMP_TIME, CMPNY_MEMO, REVISION_MEMO,
/* 견적서 금액 */ /* 신청서에서는 가격정보 보여주지 않음 #12 */ /* QUO_TB.VAT_YN, QUO_TB.COST_TOTAL, QUO_TB.TOTAL_VAT, */
APP_YEAR, APP_MON, APP_DAY, APP_NAME, FN_GET_IMAGE(APP_SIGN_URL,0) APP_SIGN_URL, APP_AGREE_NAME, FN_GET_IMAGE(APP_AGREE_SIGN_URL,0) APP_AGREE_SIGN_URL, WORK_CHK_YN, WORK_NAME, FN_GET_IMAGE(WORK_SIGN_URL,0) WORK_SIGN_URL, WORK_SIGN_URL ATCH_FILE_ID,
MOD_CHECK1_YN, MOD_CHECK2_YN,
im.inCnt, im.outCnt
FROM SBK_TB
INNER JOIN JOB_TB ON SBK_TB.SBK_YM = JOB_TB.SBK_YM AND SBK_TB.TYPE = JOB_TB.SBK_TYPE AND SBK_TB.SBK_SEQ = JOB_TB.SBK_SEQ AND SBK_TB.REVISION = JOB_TB.SBK_REVISION
LEFT JOIN QUO_TB ON QUO_TB.QUO_YM = JOB_TB.QUO_YM AND QUO_TB.QUO_SEQ = JOB_TB.QUO_SEQ
LEFT JOIN ( SELECT SBK_ID, sum(qty) inCnt, SUM(IF (CARRY_OUT_CODE IS NOT NULL, QTY, 0)) outCnt FROM IM_TB LEFT JOIN IM_SUB_TB ON IM_SUB_TB.IM_ID = CONCAT ('IM', IM_TB.IM_YM, '-', 'G', LPAD(IM_TB.IM_SEQ,4,0)) WHERE DIV_CODE = 1 AND IM_SUB_TB.STATE != 'D' GROUP BY SBK_ID) im ON CONCAT ('SB', SBK_TB.SBK_YM, '-' , SBK_TB.TYPE, LPAD(SBK_TB.SBK_SEQ,4,0), IF(REVISION > 0, CONCAT ('-',REVISION), '')) = im.SBK_ID
LEFT JOIN CMPY_TB ON JOB_TB.CMPY_SEQ = CMPY_TB.CMPY_SEQ LEFT JOIN CMPY_MNG_TB ON JOB_TB.CMPY_MNG_SEQ = CMPY_MNG_TB.CMPY_MNG_SEQ
/* 보안견적서 기책있는지 확인 */ LEFT JOIN TEST_ITEM_TB ON JOB_TB.QUO_YM = TEST_ITEM_TB.QUO_YM AND JOB_TB.QUO_SEQ = TEST_ITEM_TB.QUO_SEQ AND TEST_ITEM_TB.STATE != 'D' AND TEST_ITEM_SEQ = (SELECT MAX(TEST_ITEM_SEQ) FROM TEST_ITEM_TB WHERE JOB_TB.QUO_YM = QUO_YM AND JOB_TB.QUO_SEQ = QUO_SEQ AND REV_ID = #{memId} AND STATE != 'D')
WHERE 1=1 /* 보안견적서 */ AND IF(JOB_TB.SECRET_YN = 1, #{memId} IN (JOB_TB.MNG_ID, TEST_ITEM_TB.REV_ID), 1)
AND JOB_TB.quo_ym = SUBSTRING(#{quoId},2,4) AND JOB_TB.quo_seq = SUBSTRING(#{quoId},8,4)
AND SBK_TB.SBK_YM = SUBSTRING(#{sbkId},3,2) AND SBK_TB.TYPE = SUBSTRING(#{sbkId},6,1) AND SBK_TB.SBK_SEQ = SUBSTRING(#{sbkId},7,4) AND SBK_TB.REVISION = SUBSTRING(#{sbkId},12,1)

mapper.insert:
SELECT MAX(JOB_SEQ) jobSeq FROM JOB_TB;
INSERT INTO JOB_TB ( SBK_YM, SBK_TYPE, SBK_SEQ,
/* 구버전 */ CMPY_SEQ, CMPY_MNG_SEQ, CMPY_NAME, MNG_NAME, MNG_PHONE, MNG_FAX, MNG_EMAIL,
/* 신버전 */ PRTN_SEQ, PRTN_MNG_SEQ, PRTN_YN, DIRT_SEQ, DIRT_MNG_SEQ,
MNG_ID, PRDCT_NAME, MODEL_NAME, INS_MEM_ID, UDT_MEM_ID ) VALUES ( SUBSTRING(#{sbkId},3,2), SUBSTRING(#{sbkId},6,1), SUBSTRING(#{sbkId},7,4),
