#{mngId}, #{prdctName}, #{modelName}, #{insMemId}, #{udtMemId} )

SELECT IFNULL (MAX(SBK_SEQ),0)+1 FROM SBK_TB WHERE SBK_YM = DATE_FORMAT(NOW(), '%y') AND TYPE = #{type}
INSERT INTO SBK_TB ( SBK_YM, SBK_SEQ, TYPE,
EST_CMP_DT,
BSNS_RGNMB, RPRSN, CRPRT_RGNMB, ADDRESS, RSDNT_RGNMB, INDST_MF_YN, INDST_SL_YN, INDST_IP_YN, MNG_TEL, EXTEND_MODEL, CMPNY_IDNTF, ATHNT_NMBR, NEW_CMPNY_IDNTF1, NEW_CMPNY_IDNTF2, NEW_CMPNY_IDNTF3, ELCTR_RTNG_W, CLOCK_FRQNC, MDL_IDNTF, ADD_DEV, MNFCT_CMPNY, MNFCT_CNTRY_CODE, MNFCT_ADRES, ADD_MNFCT_CMPNY, ADD_MNFCT_CNTRY, TEST_PLACE_IN_YN, TEST_PLACE_OUT_YN, TEST_ADRES, ELCTR_TEST_C_YN, ELCTR_TEST_K_YN, ELCTR_TEST_N_YN, TEST_RPRT_K_YN, TEST_RPRT_N_YN, TEST_RPRT_JODAL_YN, TEST_RPRT_SPRT_YN, TEST_RPRT_SPRT, TEST_RPRT_ETC_YN, TEST_RPRT_ETC, CNFRM_YN, IM_EUT_YN, IM_CLNT_YN, IM_DLVRY_YN, IM_DSPSL_YN, IM_ETC_YN, CUS_INFO_AGREE_YN, EST_CMP_TIME, INS_MEM_ID, UDT_MEM_ID, SG_NEW_YN, SG_GB_YN, SG_DG_YN, SG_ETC_YN, CF_JD_YN, CF_JI_YN, CF_JG_YN, CF_JJ_YN, CF_ETC1_YN, CF_ETC1, CF_AI_YN, CF_AH_YN, CF_GJ_YN, CF_DJ_YN, CF_EH_YN, CF_ETC2_YN, CF_ETC2, CF_CD_YN, CF_CC_YN, CF_FCC_YN, CF_VCCI_YN, CF_PSE_YN, TEST_EMC_YN, TEST_EM_YN, TEST_EMF_YN, TEST_TEL_YN, TEST_ROHS_YN, TEST_SS_YN, TEST_SAF_YN, TEST_SM_YN, TEST_DJ_YN, TEST_EH_YN, TEST_RF_YN, TEST_SAR_YN, TEST_OUT_YN, TEST_IN_YN, CLASS_A_YN, CLASS_B_YN, PP_YN, PP_NAME, PP_NUM, PP_BL, PP_CNT, DOC_URL, DOC_IS_YN, DOC_SS_YN, DOC_HD_YN, DOC_BB_YN, DOC_PD_YN, DOC_Bl_YN, DOC_JB_YN, DOC_JS_YN, DOC_CR_YN, DOC_SI_YN, DOC_SG_YN, DOC_SB_YN, APP_YEAR, APP_MON, APP_DAY, APP_NAME, APP_SIGN_URL, APP_AGREE_NAME, APP_AGREE_SIGN_URL, WORK_CHK_YN, WORK_NAME, WORK_SIGN_URL, MOD_CHECK1_YN, MOD_CHECK2_YN, CMPNY_MEMO ) VALUES ( DATE_FORMAT(NOW(), '%y'), #{sbkSeq}, #{type},
#{estCmpDt},
#{bsnsRgnmb}, #{rprsn}, #{crprtRgnmb}, #{address}, #{rsdntRgnmb}, #{indstMfYn}, #{indstSlYn}, #{indstIpYn}, #{mngTel}, #{extendModel}, #{cmpnyIdntf}, #{athntNmbr}, #{newCmpnyIdntf1}, #{newCmpnyIdntf2}, #{newCmpnyIdntf3}, #{elctrRtngW}, #{clockFrqnc}, #{mdlIdntf}, #{addDev}, #{mnfctCmpny}, #{mnfctCntryCode}, #{mnfctAdres}, #{addMnfctCmpny}, #{addMnfctCntry}, #{testPlaceInYn}, #{testPlaceOutYn}, #{testAdres}, #{elctrTestCYn}, #{elctrTestKYn}, #{elctrTestNYn}, #{testRprtKYn}, #{testRprtNYn}, #{testRprtJodalYn}, #{testRprtSprtYn}, #{testRprtSprt}, #{testRprtEtcYn}, #{testRprtEtc}, #{cnfrmYn}, #{imEutYn}, #{imClntYn}, #{imDlvryYn}, #{imDspslYn}, #{imEtcYn}, #{cusInfoAgreeYn}, #{estCmpTime}, #{insMemId}, #{udtMemId}, #{sgNewYn}, #{sgGbYn}, #{sgDgYn}, #{sgEtcYn}, #{cfJdYn}, #{cfJiYn}, #{cfJgYn}, #{cfJjYn}, #{cfEtc1Yn}, #{cfEtc1}, #{cfAiYn}, #{cfAhYn}, #{cfGjYn}, #{cfDjYn}, #{cfEhYn}, #{cfEtc2Yn}, #{cfEtc2}, #{cfCdYn}, #{cfCcYn}, #{cfFccYn}, #{cfVcciYn}, #{cfPseYn}, #{testEmcYn}, #{testEmYn}, #{testEmfYn}, #{testTelYn}, #{testRohsYn}, #{testSsYn}, #{testSafYn}, #{testSmYn}, #{testDjYn}, #{testEhYn}, #{testRfYn}, #{testSarYn}, #{testOutYn}, #{testInYn}, #{classAYn}, #{classBYn}, #{ppYn}, #{ppName}, #{ppNum}, #{ppBl}, #{ppCnt}, #{docUrl}, #{docIsYn}, #{docSsYn}, #{docHdYn}, #{docBbYn}, #{docPdYn}, #{docBlYn}, #{docJbYn}, #{docJsYn}, #{docCrYn}, #{docSiYn}, #{docSgYn}, #{docSbYn}, DATE_FORMAT(NOW(), '%Y'), DATE_FORMAT(NOW(), '%m'), DATE_FORMAT(NOW(), '%d'), #{appName}, #{appSignUrl}, #{appAgreeName}, #{appAgreeSignUrl}, #{workChkYn}, #{workName}, #{atchFileId}, #{modCheck1Yn}, #{modCheck2Yn}, #{cmpnyMemo} );

mapper.select:
SELECT COUNT(1) FROM ( SELECT COUNT(1)
WHERE 1=1
/* 보안견적서 */ AND IF(JOB_TB.SECRET_YN = 1, #{memId} IN (JOB_TB.MNG_ID, TEST_ITEM_TB.REV_ID), 1)
GROUP BY SBK_TB.SBK_YM, SBK_TB.TYPE, SBK_TB.SBK_SEQ, SBK_TB.REVISION
) cnt

set @ROWNUM:=0; SELECT @ROWNUM:=@ROWNUM+1 AS NO, lst.* FROM (
SELECT
JOB_TB.JOB_SEQ AS JOB_SEQ, SBK_TB.SBK_YM AS SBK_YM, SBK_TB.SBK_SEQ AS SBK_SEQ, SBK_TB.TYPE AS TYPE, JOB_TB.SECRET_YN, IFNULL(CODE_CP.TYPE_NAME, '변경') AS stateCode, IFNULL(DATE_FORMAT(JOB_TB.STATE_UDT_DT, '%Y-%m-%d'), '') AS stateUdtDt, SBK_TB.INS_DT AS INS_DT, DATE_FORMAT(SBK_TB.INS_DT, '%Y-%m-%d') AS insDtStr, CONCAT('SB', SBK_TB.SBK_YM, '-', SBK_TB.TYPE, LPAD(SBK_TB.SBK_SEQ, 4, 0), if((SBK_TB.REVISION > 0), CONCAT('-', SBK_TB.REVISION), '')) AS sbkId, SBK_TB.REVISION AS REVISION, IFNULL(DATE_FORMAT(SBK_TB.EST_CMP_DT, '%Y-%m-%d'), '-') AS estCmpDt, JOB_TB.CMPY_SEQ AS CMPY_SEQ, IFNULL(CODE_PT.TYPE_NAME, '미등록') AS cmpyType, IFNULL(CMPY_TB.CMPY_NAME, '') AS cmpyTitle, IFNULL(JOB_TB.CMPY_NAME, '') AS cmpyName, JOB_TB.PRDCT_NAME AS PRDCT_NAME, CONCAT_WS(',', if((SBK_TB.SG_NEW_YN = 1), '신규', NULL), if((SBK_TB.SG_GB_YN = 1), '기술기준 변경', NULL), if((SBK_TB.SG_DG_YN = 1), '동일기자재', NULL), if((SBK_TB.SG_ETC_YN = 1), '기술기준외 변경', NULL)) AS sgText, FN_MEM_NAME(JOB_TB.MNG_ID) AS memName, IFNULL(JOB_TB.CNS_SEQ, '') AS CNS_SEQ, IFNULL(FN_LAST_CNS_MENO(JOB_TB.CNS_SEQ), '') AS lastMemo, if((SBK_TB.TEST_PLACE_OUT_YN = 1), '현장시험', IFNULL(DATE_FORMAT(IM_TOTAL_V.IN_DATE, '%Y-%m-%d'), '-')) AS carryInDt,
/* 시험항목 및 비용 */ COUNT(TEST_ITEM_TB.TEST_ITEM_SEQ) TEST_ITEM_CNT, TEST_ITEM_TB.TEST_ITEM_SEQ AS TEST_ITEM_SEQ, cate1.TEST_CATE_SEQ AS crtfc1, cate2.TEST_CATE_SEQ AS crtfc2, cate3.TEST_CATE_SEQ AS crtfc3, cate4.TEST_CATE_SEQ AS crtfc4, cate1.NAME AS crtfc1Name, IFNULL(cate2.NAME, '') AS crtfc2Name, IFNULL(cate3.NAME, '') AS crtfc3Name, IFNULL(cate4.NAME, '') AS crtfc4Name, TEST_ITEM_TB.TEST_TYPE_CODE AS TEST_TYPE_CODE, TEST_STNDR_TB.TEST_STNDR_SEQ AS TEST_STNDR_SEQ, if((TEST_ITEM_TB.TEST_STNDR_SEQ = 0), TEST_ITEM_TB.TEST_STNDR_TEXT, IFNULL(TEST_STNDR_TB.TEST_STNDR, '')) AS TEST_STNDR, IFNULL(CODE_TT.TYPE_NAME, '') AS TEST_TYPE, IFNULL(TEST_ITEM_TB.MODEL, '') AS MODEL, IFNULL(DATE_FORMAT(TEST_ITEM_TB.EST_CMP_DT, '%Y-%m-%d'), '') AS ITEM_EST_CMP_DT, TEST_ITEM_TB.SIGN_STATE_CODE AS SIGN_STATE_CODE, IF(IF(TEST_ITEM_TB.REV_SIGN_URL = '', NULL, TEST_ITEM_TB.REV_SIGN_URL) IS NULL, IFNULL(CODE_SS.TYPE_NAME, '-'), '서명완료') AS SIGN_STATE, IFNULL(FN_DEPT_NAME(TEST_TB.INS_MEM_ID), '') AS testMemDept, CONCAT(FN_POS_NAME(TEST_TB.INS_MEM_ID), ' ', FN_MEM_NAME(TEST_TB.INS_MEM_ID)) AS testMem, IFNULL(DATE_FORMAT(TEST_TB.INS_DT, '%Y-%m-%d'), '') AS testInDt, IFNULL(CODE_TS.TYPE_NAME, '-') AS rawYn, IFNULL(TEST_TB.TEST_SEQ, 0) AS TEST_SEQ, (CASE WHEN (ISNULL(TEST_TB.TEST_SEQ) AND (IFNULL(TEST_ITEM_TB.TEST_TYPE_CODE, '') != 'DH')) THEN '신청하기' WHEN (TEST_ITEM_TB.TEST_TYPE_CODE = 'DH') THEN '불필요' ELSE CODE_TT.TYPE_NAME END) AS TEST_STATE, CMPY_TB.CMPY_CODE AS CMPY_CODE, JOB_TB.MNG_ID AS MNG_ID, SBK_TB.EST_CMP_DT AS EST_CMP_DT, IM_TOTAL_V.IN_DATE AS IN_DATE, SBK_TB.SG_NEW_YN AS SG_NEW_YN, SBK_TB.SG_GB_YN AS SG_GB_YN, SBK_TB.SG_DG_YN AS SG_DG_YN, SBK_TB.SG_ETC_YN AS SG_ETC_YN, TEST_TB.TEST_STATE_CODE AS TEST_STATE_CODE, TEST_ITEM_TB.REV_SIGN_URL AS REV_SIGN_URL, JOB_TB.STATE_CODE AS STATE_CODE
WHERE 1=1
/* 보안견적서 */ AND IF(JOB_TB.SECRET_YN = 1, #{memId} IN (JOB_TB.MNG_ID, TEST_ITEM_TB.REV_ID), 1)
GROUP BY SBK_TB.SBK_YM, SBK_TB.TYPE, SBK_TB.SBK_SEQ, SBK_TB.REVISION ORDER BY SBK_TB.INS_DT, SBK_TB.REVISION
) lst ORDER BY NO DESC
LIMIT #{pageUnit} OFFSET #{firstIndex};

SELECT
/* 시험항목 및 비용 */ TEST_ITEM_TB.TEST_ITEM_SEQ AS TEST_ITEM_SEQ, cate1.TEST_CATE_SEQ AS crtfc1, cate2.TEST_CATE_SEQ AS crtfc2, cate3.TEST_CATE_SEQ AS crtfc3, cate4.TEST_CATE_SEQ AS crtfc4, cate1.NAME AS crtfc1Name, IFNULL(cate2.NAME, '') AS crtfc2Name, IFNULL(cate3.NAME, '') AS crtfc3Name, IFNULL(cate4.NAME, '') AS crtfc4Name, TEST_ITEM_TB.TEST_TYPE_CODE AS TEST_TYPE_CODE, TEST_STNDR_TB.TEST_STNDR_SEQ AS TEST_STNDR_SEQ, if((TEST_ITEM_TB.TEST_STNDR_SEQ = 0), TEST_ITEM_TB.TEST_STNDR_TEXT, IFNULL(TEST_STNDR_TB.TEST_STNDR, '')) AS TEST_STNDR, IFNULL(CODE_TT.TYPE_NAME, '') AS TEST_TYPE, IFNULL(TEST_ITEM_TB.MODEL, '') AS MODEL, IFNULL(DATE_FORMAT(TEST_ITEM_TB.EST_CMP_DT, '%Y-%m-%d'), '') AS ITEM_EST_CMP_DT, TEST_ITEM_TB.SIGN_STATE_CODE AS SIGN_STATE_CODE, IF(IF(TEST_ITEM_TB.REV_SIGN_URL = '', NULL, TEST_ITEM_TB.REV_SIGN_URL) IS NULL, IFNULL(CODE_SS.TYPE_NAME, '-'), '서명완료') AS SIGN_STATE, IFNULL(FN_DEPT_NAME(TEST_TB.INS_MEM_ID), '') AS testMemDept, CONCAT(FN_POS_NAME(TEST_TB.INS_MEM_ID), ' ', FN_MEM_NAME(TEST_TB.INS_MEM_ID)) AS testMem, IFNULL(DATE_FORMAT(TEST_TB.INS_DT, '%Y-%m-%d'), '') AS testInDt, IFNULL(CODE_TS.TYPE_NAME, '-') AS rawYn, IFNULL(TEST_TB.TEST_SEQ, 0) AS TEST_SEQ, (CASE WHEN (ISNULL(TEST_TB.TEST_SEQ) AND (IFNULL(TEST_ITEM_TB.TEST_TYPE_CODE, '') != 'DH')) THEN '신청하기' WHEN (TEST_ITEM_TB.TEST_TYPE_CODE = 'DH') THEN '불필요' ELSE CODE_TT.TYPE_NAME END) AS TEST_STATE, CMPY_TB.CMPY_CODE AS CMPY_CODE, JOB_TB.MNG_ID AS MNG_ID, SBK_TB.EST_CMP_DT AS EST_CMP_DT, IM_TOTAL_V.IN_DATE AS IN_DATE, SBK_TB.SG_NEW_YN AS SG_NEW_YN, SBK_TB.SG_GB_YN AS SG_GB_YN, SBK_TB.SG_DG_YN AS SG_DG_YN, SBK_TB.SG_ETC_YN AS SG_ETC_YN, TEST_TB.TEST_STATE_CODE AS TEST_STATE_CODE, TEST_ITEM_TB.REV_SIGN_URL AS REV_SIGN_URL
WHERE 1=1 AND CONCAT ('SB', SBK_TB.SBK_YM, '-' , SBK_TB.TYPE, LPAD(SBK_TB.SBK_SEQ,4,0), IF(REVISION > 0, CONCAT ('-',REVISION), '')) = #{sbkId}
