mapper.select:
SELECT IFNULL(MAX(MGMT_NO), 0) + 1 
FROM MACHINE_TB 
WHERE MGMT_DEPT = #{mgmtDept}

update.foreach:
UPDATE MACHINE_CAL_INFO
SET CAL_FILE = #{item.calFile}
WHERE MACHINE_CAL_SEQ = #{item.machineCalSeq}

mapper.insert:
INSERT INTO MACHINE_CAL_INFO (
    MACHINE_SEQ, CAL_TGT, CAL_DT, CAL_ORG, CAL_NO, CAL_FILE,
    REFORM_DT, REFORM_PERIOD, INS_MEM_ID, INS_DT, UDT_MEM_ID, UDT_DT, STATE
) VALUES (
    #{machineSeq}, #{calTgt}, NULLIF(#{calDt}, ''), #{calOrg}, #{calNo}, #{calFile},
    NULLIF(#{reformDt}, ''), #{reformPeriod}, #{insMemId}, NOW(), #{udtMemId}, NOW(), #{state}
);

mapper.select:
SELECT MACHINE_CAL_SEQ, MACHINE_SEQ, FN_CODE_NAME(CAL_TGT, 'MT') CAL_TGT, CAL_DT, CAL_ORG, CAL_NO, CAL_FILE, CASE WHEN CAL_FILE IS NOT NULL THEN CONCAT('/file/fileDown.do?atchFileId=', CAL_FILE, '&fileSn=0') ELSE '-' END AS CAL_FILE_LINK, COALESCE(fd.ORIGNL_FILE_NM, '-') AS CAL_FILE_NM, REFORM_DT, FN_CODE_NAME(REFORM_PERIOD, 'MY') REFORM_PERIOD_NAME FROM MACHINE_CAL_INFO
LEFT JOIN FILE_TB ft ON CAL_FILE = ft.ATCH_FILE_ID AND ft.USE_AT = 'Y' LEFT JOIN FILE_DETAIL_TB fd ON ft.ATCH_FILE_ID = fd.ATCH_FILE_ID AND fd.STATE != 'D'
WHERE MACHINE_SEQ = #{machineSeq} ORDER BY MACHINE_CAL_SEQ DESC;

mapper.update:
UPDATE MACHINE_TB
SET CAL_FILE = ''
WHERE MACHINE_SEQ = #{machineSeq} AND CAL_FILE = #{delFile.atchFileId}

UPDATE MACHINE_CAL_INFO
SET CAL_FILE = ''
WHERE MACHINE_SEQ = #{machineSeq} AND CAL_FILE = #{delFile.atchFileId}

mapper.insert:
UPDATE MACHINE_TB SET
ESD_ORDR = #{disOrdr}, RS_ORDR = #{disOrdr}, EFT_ORDR = #{disOrdr}, SURGE_ORDR = #{disOrdr}, CS_ORDR = #{disOrdr}, MFLD_ORDR = #{disOrdr}, VDIP_ORDR = #{disOrdr}, CTI_ORDR = #{disOrdr}, CEA_ORDR = #{disOrdr}, CEB_ORDR = #{disOrdr}, REA_ORDR = #{disOrdr}, REB_ORDR = #{disOrdr}, CLK_ORDR = #{disOrdr}, DP_ORDR = #{disOrdr},
STATE = 'U', UDT_DT = NOW(), UDT_MEM_ID = #{udtMemId}
WHERE MACHINE_SEQ = #{machineSeq}
