mapper.select:
SELECT count(1) FROM TEST_CATE_TB cate4 inner join TEST_CATE_TB cate3 on cate4.TOP_DEPTH_SEQ = cate3.TEST_CATE_SEQ inner join TEST_CATE_TB cate2 on cate3.TOP_DEPTH_SEQ = cate2.TEST_CATE_SEQ inner join TEST_CATE_TB cate1 on cate2.TOP_DEPTH_SEQ = cate1.TEST_CATE_SEQ where 1 = 1 and cate4.state != 'D' and cate4.depth = 4
and date_format(cate4.ins_dt, '%Y%m%d') between date_format(#{item.startDate}, '%Y%m%d') and date_format(#{item.endDate}, '%Y%m%d')
and cate1.TEST_CATE_SEQ = #{item.searchWord}
and cate2.TEST_CATE_SEQ = #{item.searchWord}
and cate3.TEST_CATE_SEQ = #{item.searchWord}
and cate4.TEST_CATE_SEQ = #{item.searchWord}
and ifnull(FN_MEM_NAME(cate4.INS_MEM_ID), '') like concat('%',#{item.searchWord},'%')

set @rownum:=0;
SELECT @rownum:=@rownum+1 as no, lst.* FROM ( SELECT cate4.TEST_CATE_SEQ, cate1.name depth1Text, cate2.name depth2Text, cate3.name depth3Text, cate4.name depth4Text, cate4.memo, date_format (cate4.ins_dt, '%Y-%m-%d') insDtStr, IFNULL(FN_MEM_NAME (cate4.ins_mem_id), '') memName FROM TEST_CATE_TB cate4 inner join TEST_CATE_TB cate3 on cate4.TOP_DEPTH_SEQ = cate3.TEST_CATE_SEQ inner join TEST_CATE_TB cate2 on cate3.TOP_DEPTH_SEQ = cate2.TEST_CATE_SEQ inner join TEST_CATE_TB cate1 on cate2.TOP_DEPTH_SEQ = cate1.TEST_CATE_SEQ where 1 = 1 and cate4.state != 'D' and cate4.depth = 4
and date_format(cate4.ins_dt, '%Y%m%d') between date_format(#{item.startDate}, '%Y%m%d') and date_format(#{item.endDate}, '%Y%m%d')
and cate1.TEST_CATE_SEQ = #{item.searchWord}
and cate2.TEST_CATE_SEQ = #{item.searchWord}
and cate3.TEST_CATE_SEQ = #{item.searchWord}
and cate4.TEST_CATE_SEQ = #{item.searchWord}
and ifnull(FN_MEM_NAME(cate4.INS_MEM_ID), '') like concat('%',#{item.searchWord},'%')
order by cate4.TEST_CATE_SEQ ASC ) lst ORDER BY no desc
