mapper.select:
select test_stndr_seq, test_stndr
from TEST_STNDR_TB
where use_yn = 1
and country_code = #{countryCode}
and crtf_type_seq = #{crtfTypeSeq}
and test_type_code = #{testTypeCode}

SELECT
CONCAT ('SB', SBK_TB.SBK_YM, '-' , SBK_TB.TYPE, LPAD(SBK_TB.SBK_SEQ,4,0), IF(REVISION > 0, CONCAT ('-',REVISION), '')) sbkId,
/* 신청기기 */ JOB_TB.PRDCT_NAME, JOB_TB.MODEL_NAME, EXTEND_MODEL, SBK_TB.CMPNY_IDNTF, ATHNT_NMBR, NEW_CMPNY_IDNTF1, NEW_CMPNY_IDNTF2, NEW_CMPNY_IDNTF3, ELCTR_RTNG_W, CLOCK_FRQNC, MDL_IDNTF, ADD_DEV,
/* 23-10-12 : 내용추가 */ CMPNY_MEMO, CLASS_A_YN, CLASS_B_YN, IFNULL (CMPY_TB.CMPY_NAME, '') CMPY_TITLE,
/* 23-10-20 : 내용추가 */ JOB_TB.CMPY_NAME, ADDRESS, MNFCT_CMPNY, MNFCT_ADRES,
TEST_RPRT_K_YN, TEST_RPRT_N_YN, ELCTR_TEST_C_YN, ELCTR_TEST_K_YN, ELCTR_TEST_N_YN
FROM SBK_TB
INNER JOIN JOB_TB ON SBK_TB.SBK_YM = JOB_TB.SBK_YM AND SBK_TB.TYPE = JOB_TB.SBK_TYPE AND SBK_TB.SBK_SEQ = JOB_TB.SBK_SEQ AND SBK_TB.REVISION = JOB_TB.SBK_REVISION
LEFT JOIN CMPY_TB ON JOB_TB.CMPY_SEQ = CMPY_TB.CMPY_SEQ AND CMPY_CODE = '0000'
WHERE 1=1 AND SBK_TB.SBK_YM = SUBSTRING(#{sbkId},3,2) AND SBK_TB.TYPE = SUBSTRING(#{sbkId},6,1) AND SBK_TB.SBK_SEQ = SUBSTRING(#{sbkId},7,4) AND SBK_TB.REVISION = SUBSTRING(#{sbkId},12,1)
