mapper.select:
set @rownum:=0;
SELECT @rownum:=@rownum+1 as no, lst.* FROM ( SELECT CMPY_SEQ, date_format (CMPY_TB.INS_DT, '%Y-%m-%d') insDtStr, lpad (CMPY_TB.cmpy_seq, 4, '0') cmpySeq, CMPY_TB.cmpy_name, ifnull (FN_CODE_NAME (type_code, 'PK'), '') partnerType, ifnull (FN_CODE_NAME (type_code, 'ST'), '') directType, ifnull (FN_CODE_NAME (cntry_code, 'SN'), '') cntry, FN_GET_PHONE_FORMAT (cmpy_phone) cmpy_phone, ifnull (memo, '-') memo, ifnull (FN_MEM_NAME (CMPY_TB.INS_MEM_ID), '') ins_name FROM CMPY_TB WHERE 1 = 1
AND CMPY_CODE = #{item.searchWord}
and IFNULL(FN_MEM_NAME(CMPY_TB.INS_MEM_ID), '') like concat('%',#{item.searchWord},'%')
AND cmpy_name like concat('%',#{item.searchWord},'%')
AND cmpy_phone like concat('%',replace(#{item.searchWord},'-',''),'%')
AND IFNULL(type_code,'9999') = #{item.searchWord}
and date_format(CMPY_TB.ins_dt, '%Y%m%d') between date_format(#{item.startDate}, '%Y%m%d') and date_format(#{item.endDate}, '%Y%m%d')
order by CMPY_TB.CMPY_SEQ ASC ) lst ORDER BY no desc LIMIT #{pageUnit} OFFSET #{firstIndex};

update.foreach:
UPDATE CMPY_MNG_TB
SET
	STATE = #{item.state}
WHERE CMPY_MNG_SEQ = #{item.cmpyMngSeq}
