mapper.select:
set @rownum:=0;
select lst.* from ( select @rownum:=@rownum+1 as no, #ROW_NUMBER() OVER(ORDER BY CNS_TB.ins_dt) AS no, CNS_TB.CNS_SEQ, date_format(CNS_TB.INS_DT, '%Y-%m-%d %T') ins_dt_str, IFNULL(FN_CODE_NAME(CMPY_TB.CMPY_CODE,'PT'), '') cmpy_type, ifnull(FN_MEM_NAME(CNS_TB.INS_MEM_ID), '') ins_name, CRTFC_TYPE, JOB_TB.PRDCT_NAME,
/* 업체정보 */ IFNULL(CMPY_TB.CMPY_NAME, JOB_TB.CMPY_NAME) CMPY_NAME, FN_GET_PHONE_FORMAT(IFNULL(CMPY_TB.CMPY_PHONE, JOB_TB.CMPY_PHONE)) CMPY_PHONE, FN_GET_PHONE_FORMAT(IFNULL(CMPY_MNG_TB.PHONE, JOB_TB.MNG_PHONE)) MNG_PHONE,
/* 연결문서 정보 */ IFNULL(FN_GET_SEQSTRING('Q',JOB_TB.QUO_YM,'G',JOB_TB.quo_seq),'-') QUO_ID, IFNULL(FN_GET_SEQSTRING('SB',JOB_TB.SBK_YM,'G',JOB_TB.sbk_seq),'-') SBK_ID,
FN_LAST_CNS_MENO(CNS_TB.cns_seq) LAST_MEMO from CNS_TB inner join JOB_TB on CNS_TB.CNS_SEQ = JOB_TB.CNS_SEQ left join CMPY_TB on JOB_TB.CMPY_SEQ = CMPY_TB.CMPY_SEQ left join CMPY_MNG_TB on JOB_TB.CMPY_MNG_SEQ = CMPY_MNG_TB.CMPY_MNG_SEQ and CMPY_MNG_TB.STATE != 'D' where 1=1
and ifnull(CMPY_TB.CMPY_CODE,'9999') = #{item.searchWord}
and ifnull(FN_MEM_NAME(CNS_TB.INS_MEM_ID), '') like concat('%',#{item.searchWord},'%')
and PRDCT_NAME like concat('%',#{item.searchWord},'%')
and CRTFC_TYPE like concat('%',#{item.searchWord},'%')
and IFNULL(CMPY_TB.CMPY_NAME, JOB_TB.CMPY_NAME) like concat('%',#{item.searchWord},'%')
and FN_GET_PHONE_FORMAT(IFNULL(CMPY_TB.CMPY_PHONE, JOB_TB.CMPY_PHONE)) like concat('%',#{item.searchWord},'%')
and FN_GET_PHONE_FORMAT(IFNULL(CMPY_MNG_TB.PHONE, JOB_TB.MNG_PHONE)) like concat('%',#{item.searchWord},'%')
and date_format(CNS_TB.ins_dt, '%Y%m%d') between date_format(#{item.startDate}, '%Y%m%d') and date_format(#{item.endDate}, '%Y%m%d')
order by cns_seq asc ) lst order by no desc LIMIT #{pageUnit} OFFSET #{firstIndex};

mapper.insert:

INSERT INTO CNS_HIS_TB
(CNS_SEQ,
MEMO,
INS_MEM_ID,
UDT_MEM_ID
)
VALUES
(#{cnsSeq},
#{memo},
#{insMemId},
#{udtMemId}
);

mapper.update:
UPDATE JOB_TB SET CMPY_SEQ = #{cmpySeq}, CMPY_MNG_SEQ = #{cmpyMngSeq},
CMPY_NAME = #{cmpyName}, CMPY_PHONE = replace(#{cmpyPhone},'-',''), MNG_NAME = #{mngName}, MNG_PHONE = replace(#{mngPhone},'-',''),
PRDCT_NAME = #{prdctName}, UDT_MEM_ID = #{udtMemId}, UDT_DT = current_timestamp(), STATE = 'U' WHERE CNS_SEQ = #{cnsSeq};
