mapper.select:
SELECT
JOB_TB.sbk_ym, JOB_TB.sbk_seq, FN_GET_SEQSTRING('SB',SBK_TB.SBK_YM,SBK_TB.type,SBK_TB.SBK_SEQ) sbkId , IFNULL(FN_GET_SEQSTRING('Q',JOB_TB.QUO_YM,'G',JOB_TB.QUO_SEQ), '') quoId, IFNULL(date_format(SBK_TB.est_cmp_dt, '%Y-%m-%d'), '-') estCmpDt,
/* 업체정보 */ ifnull((select cmpy_code from CMPY_TB where cmpy_seq = JOB_TB.CMPY_SEQ),'') cmpy_code, fn_cust_type(JOB_TB.CMPY_SEQ) cmpyType, JOB_TB.CMPY_SEQ,
/* 신청인 */ JOB_TB.cmpy_name, BSNS_RGNMB, RPRSN, CRPRT_RGNMB, ADDRESS, RSDNT_RGNMB, INDST_MF_YN, INDST_SL_YN, INDST_IP_YN, JOB_TB.mng_name, MNG_EMAIL, FN_GET_PHONE_FORMAT(ifnull(MNG_TEL, '')) MNG_TEL, FN_GET_PHONE_FORMAT(ifnull(JOB_TB.MNG_FAX, '')) MNG_FAX, FN_GET_PHONE_FORMAT(ifnull(JOB_TB.mng_phone, '')) mng_phone,
/* 신청기기 */ JOB_TB.prdct_name, JOB_TB.model_name, EXTEND_MODEL, CMPNY_IDNTF, ATHNT_NMBR, NEW_CMPNY_IDNTF1, NEW_CMPNY_IDNTF2, NEW_CMPNY_IDNTF3, ELCTR_RTNG_W, CLOCK_FRQNC, MDL_IDNTF, ADD_DEV,
/* 제조자 */ MNFCT_CMPNY, MNFCT_CNTRY_CODE, MNFCT_ADRES, ADD_MNFCT_CMPNY, ADD_MNFCT_CNTRY,
/* 시험 */ TEST_PLACE_IN_YN, TEST_PLACE_OUT_YN, TEST_ADRES, ELCTR_TEST_C_YN, ELCTR_TEST_K_YN, ELCTR_TEST_N_YN, TEST_RPRT_K_YN, TEST_RPRT_N_YN, TEST_RPRT_JODAL_YN, TEST_RPRT_SPRT_YN, TEST_RPRT_SPRT, TEST_RPRT_ETC_YN, TEST_RPRT_ETC,
/* 신청규격 */ SG_NEW_YN, SG_GB_YN, SG_DG_YN, SG_ETC_YN, CF_JD_YN, CF_JI_YN, CF_JG_YN, CF_JJ_YN, CF_ETC1_YN, CF_ETC1, CF_AI_YN, CF_AH_YN, CF_GJ_YN, CF_DJ_YN, CF_EH_YN, CF_ETC2_YN, CF_ETC2, CF_CD_YN, CF_CC_YN, CF_FCC_YN, CF_VCCI_YN, CF_PSE_YN, TEST_EMC_YN, TEST_EM_YN, TEST_EMF_YN, TEST_TEL_YN, TEST_ROHS_YN, TEST_SS_YN, TEST_SAF_YN, TEST_SM_YN, TEST_DJ_YN, TEST_EH_YN, TEST_RF_YN, TEST_SAR_YN, TEST_OUT_YN, TEST_IN_YN, CLASS_A_YN, CLASS_B_YN, PP_YN, PP_NAME, PP_NUM, PP_BL, PP_CNT, DOC_URL, DOC_IS_YN, DOC_SS_YN, DOC_HD_YN, DOC_BB_YN, DOC_PD_YN, DOC_Bl_YN, DOC_JB_YN, DOC_JS_YN, DOC_CR_YN, DOC_SI_YN, DOC_SG_YN, DOC_SB_YN, CNFRM_YN, IM_EUT_YN, IM_CLNT_YN, IM_DLVRY_YN, IM_DSPSL_YN, IM_ETC_YN, CUS_INFO_AGREE_YN, EST_CMP_TIME,
/* 견적서 금액 */ QUO_TB.VAT_YN, QUO_TB.COST_TOTAL, QUO_TB.TOTAL_VAT,
APP_YEAR, APP_MON, APP_DAY, APP_NAME, FN_GET_IMAGE(APP_SIGN_URL,0) APP_SIGN_URL, APP_AGREE_NAME, FN_GET_IMAGE(APP_AGREE_SIGN_URL,0) APP_AGREE_SIGN_URL, WORK_CHK_YN, WORK_NAME, FN_GET_IMAGE(WORK_SIGN_URL,0) WORK_SIGN_URL,
MOD_CHECK1_YN, MOD_CHECK2_YN,
im.inCnt, im.outCnt
FROM SBK_TB
INNER JOIN JOB_TB ON SBK_TB.sbk_ym = JOB_TB.sbk_ym AND SBK_TB.sbk_seq = JOB_TB.sbk_seq
LEFT JOIN QUO_TB ON QUO_TB.quo_ym = JOB_TB.quo_ym AND QUO_TB.quo_seq = JOB_TB.quo_seq
LEFT JOIN ( SELECT sbk_id, sum(qty) inCnt, sum(if(CARRY_OUT_CODE is not null,qty,0)) outCnt FROM IM_TB LEFT JOIN IM_SUB_TB ON IM_SUB_TB.im_id = FN_GET_SEQSTRING ('IM', IM_TB.IM_YM, 'G', IM_TB.IM_SEQ) WHERE DIV_CODE = 1 AND IM_SUB_TB.state != 'D' GROUP BY sbk_id) im ON FN_GET_SEQSTRING('SB',SBK_TB.SBK_YM,SBK_TB.type,SBK_TB.SBK_SEQ) = im.sbk_id
WHERE 1=1
AND JOB_TB.quo_ym = substring(#{quoId},2,4) AND JOB_TB.quo_seq = substring(#{quoId},8,4)
AND SBK_TB.sbk_ym = substring(#{sbkId},3,2) AND SBK_TB.sbk_seq = substring(#{sbkId},7,4)

set @dense_rank:=0; set @prev_value:=null; select * from ( select if( @prev_value = sbkId, @dense_rank, @dense_rank := @dense_rank + 1 ) AS no, @prev_value := sbkId, lst.* from ( SELECT #DENSE_RANK() OVER(ORDER BY SBK_TB.ins_dt) AS no, SBK_TB.ins_dt, date_format (SBK_TB.ins_dt, '%Y-%m-%d') insDtStr, FN_GET_SEQSTRING ('SB', SBK_TB.sbk_ym, type, SBK_TB.sbk_seq) sbkId, IFNULL (date_format (SBK_TB.est_cmp_dt, '%Y-%m-%d'), '-') estCmpDt, JOB_TB.CMPY_SEQ, IFNULL (FN_CODE_NAME (CMPY_TB.CMPY_CODE, 'PT'), '미등록') cmpy_type, IFNULL (CMPY_TB.CMPY_NAME, '') cmpyTitle, IFNULL (JOB_TB.CMPY_NAME, '') cmpyName, JOB_TB.PRDCT_NAME, CONCAT_WS ( ',', IF (SG_NEW_YN = 1, '신규', null), IF (SG_GB_YN = 1, '기술기준 변경', null), IF (SG_DG_YN = 1, '동일기자재', null), IF (SG_ETC_YN = 1, '기술기준외 변경', null) ) sgText, FN_MEM_NAME (SBK_TB.ins_mem_id) memName, IFNULL (JOB_TB.CNS_SEQ, '') CNS_SEQ, IFNULL (FN_LAST_CNS_MENO (JOB_TB.CNS_SEQ), '') lastMemo, IF (IM_TB.SBK_ID is null, '-' , IFNULL (DATE_FORMAT(STR_TO_DATE ( concat (SBK_TB.APP_YEAR, SBK_TB.APP_MON, SBK_TB.APP_DAY), '%Y%m%d' ),'%Y-%m-%d'), '-') ) carryInDt, /* 시험항목 및 비용 */ TEST_ITEM_TB.test_item_seq, cate1.test_cate_seq crtfc1, cate2.test_cate_seq crtfc2, cate3.test_cate_seq crtfc3, cate4.test_cate_seq crtfc4, cate1.name crtfc1Name, IFNULL (cate2.name, '') crtfc2Name, IFNULL (cate3.name, '') crtfc3Name, cate4.name crtfc4Name, TEST_ITEM_TB.TEST_TYPE_CODE, TEST_STNDR_TB.TEST_STNDR_SEQ, IFNULL (TEST_STNDR_TB.TEST_STNDR, '-') TEST_STNDR, IFNULL ( fn_code_name (TEST_ITEM_TB.TEST_TYPE_CODE, 'TT'), '-' ) TEST_TYPE, IFNULL (TEST_ITEM_TB.model, '-') model, SIGN_STATE_CODE, if ( TEST_ITEM_TB.REV_SIGN_URL is null, ifnull ( fn_code_name (TEST_ITEM_TB.SIGN_STATE_CODE, 'SS'), '-' ), '서명완료' ) SIGN_STATE, IFNULL (FN_DEPT_NAME(TEST_TB.ins_mem_id), '') testMemDept, CONCAT(FN_POS_NAME(TEST_TB.ins_mem_id), ' ',FN_MEM_NAME (TEST_TB.ins_mem_id)) testMem, IFNULL (date_format (TEST_TB.ins_dt, '%Y-%m-%d'), '') testInDt, IFNULL(FN_CODE_NAME(TEST_STATE_TB.STATE_CODE,'TS'), '-') rawYn, ifnull (TEST_TB.TEST_SEQ, 0) TEST_SEQ, if ( TEST_TB.TEST_SEQ is null, '신청하기', fn_code_name (TEST_ITEM_TB.TEST_TYPE_CODE, 'TT') ) TEST_STATE FROM SBK_TB INNER JOIN JOB_TB ON SBK_TB.SBK_YM = JOB_TB.SBK_YM AND SBK_TB.SBK_SEQ = JOB_TB.SBK_SEQ AND JOB_TB.state != 'D' LEFT JOIN CMPY_TB ON JOB_TB.CMPY_SEQ = CMPY_TB.CMPY_SEQ LEFT JOIN TEST_ITEM_TB on JOB_TB.quo_ym = TEST_ITEM_TB.quo_ym AND JOB_TB.quo_seq = TEST_ITEM_TB.quo_seq and TEST_ITEM_TB.STATE != 'D' LEFT JOIN TEST_STNDR_TB on TEST_ITEM_TB.TEST_STNDR_SEQ = TEST_STNDR_TB.TEST_STNDR_SEQ LEFT JOIN TEST_CATE_TB cate4 ON TEST_ITEM_TB.CRTF_TYPE_SEQ = cate4.TEST_CATE_SEQ LEFT JOIN TEST_CATE_TB cate3 ON cate4.TOP_DEPTH_SEQ = cate3.TEST_CATE_SEQ LEFT JOIN TEST_CATE_TB cate2 ON cate3.TOP_DEPTH_SEQ = cate2.TEST_CATE_SEQ LEFT JOIN TEST_CATE_TB cate1 ON cate2.TOP_DEPTH_SEQ = cate1.TEST_CATE_SEQ LEFT JOIN TEST_TB on TEST_TB.TEST_ITEM_SEQ = TEST_ITEM_TB.TEST_ITEM_SEQ LEFT JOIN TEST_STATE_TB ON TEST_STATE_TB.TEST_STATE_SEQ = ( select TEST_STATE_SEQ from TEST_STATE_TB sub where sub.TEST_SEQ = TEST_TB.TEST_SEQ order by TEST_STATE_SEQ desc limit 1 ) LEFT JOIN IM_TB ON IM_TB.sbk_id = FN_GET_SEQSTRING ('SB', SBK_TB.sbk_ym, type, SBK_TB.sbk_seq) where 1 = 1
and ifnull(CMPY_TB.CMPY_CODE,'9999') = #{item.searchWord}
and IFNULL(CMPY_TB.CMPY_NAME, '') like concat('%',#{item.searchWord},'%')
and FN_GET_SEQSTRING('SB',SBK_TB.sbk_ym,type,SBK_TB.sbk_seq) like concat('%',#{item.searchWord},'%')
and FN_MEM_NAME(SBK_TB.ins_mem_id) like concat('%',#{item.searchWord},'%')
and IFNULL(JOB_TB.CMPY_NAME, '') like concat('%',#{item.searchWord},'%')
and date_format(SBK_TB.ins_dt, '%Y%m%d') between date_format(#{item.startDate}, '%Y%m%d') and date_format(#{item.endDate}, '%Y%m%d')
and date_format(SBK_TB.est_cmp_dt, '%Y%m%d') between date_format(#{item.startDate}, '%Y%m%d') and date_format(#{item.endDate}, '%Y%m%d')
and IF (IM_TB.SBK_ID is null, '-' , IFNULL (DATE_FORMAT(STR_TO_DATE ( concat (SBK_TB.APP_YEAR, SBK_TB.APP_MON, SBK_TB.APP_DAY), '%Y%m%d' ),'%Y%m%d'), '-') ) between date_format(#{item.startDate}, '%Y%m%d') and date_format(#{item.endDate}, '%Y%m%d')
and cate1.TEST_CATE_SEQ = #{item.searchWord}
and cate2.TEST_CATE_SEQ = #{item.searchWord}
and cate3.TEST_CATE_SEQ = #{item.searchWord}
and cate4.TEST_CATE_SEQ = #{item.searchWord}
and SG_NEW_YN=1 and SG_GB_YN=1 and SG_DG_YN=1 and SG_ETC_YN=1
and if(TEST_TB.TEST_SEQ is null, '9999', TEST_ITEM_TB.TEST_TYPE_CODE) = #{item.searchWord}
and TEST_STATE_TB.STATE_CODE = #{item.searchWord}
and IFNULL(TEST_ITEM_TB.model, '') like concat('%',#{item.searchWord},'%')
order by SBK_TB.sbk_ym, SBK_TB.sbk_seq ) lst WHERE 1=1 order by no desc ) paging WHERE 1=1 AND no BETWEEN #{totalCount}+1-#{pageUnit}-#{firstIndex} AND #{totalCount}-#{firstIndex}
