mapper.select:
set @rownum:=0;
select @rownum:=@rownum+1 as no, lst.* from ( SELECT date_format (DEBUG_TB.ins_dt, '%Y-%m-%d') insDtStr, concat ( FN_GET_SEQSTRING ('SB', JOB_TB.sbk_ym, 'G', JOB_TB.sbk_seq), '-', TEST_TB.TEST_TYPE_CODE, LPAD (TEST_TB.TEST_NO, 4, 0) ) testId, ifnull (TEST_ITEM_TB.product, JOB_TB.PRDCT_NAME) product, TEST_ITEM_TB.model, DEBUG_TB.MEMO, concat (DATEDIFF (now(), DEBUG_TB.ins_dt), '일') runningDay, DEBUG_TB.debug_seq, IFNULL (FN_CODE_NAME (DEBUG_STATE_TB.STATE_CODE, 'TD'), '-') debugState, IFNULL (DEBUG_TB.STATE_CODE, '') STATE_CODE, IFNULL (date_format (DEBUG_TB.UDT_DT, '%Y-%m-%d'), '') uptDtStr, concat(FN_DEPT_NAME (DEBUG_TB.UDT_MEM_ID),' ',FN_MEM_NAME (DEBUG_TB.UDT_MEM_ID)) udtMem, IFNULL ( concat (DATEDIFF (DEBUG_TB.UDT_DT, DEBUG_TB.ins_dt), '일'), '' ) endDay, IFNULL (FN_CODE_NAME (DEBUG_TB.STATE_CODE, 'TR'), '') STATE, IFNULL (DEBUG_TB.ETC, '') etc FROM DEBUG_TB INNER JOIN TEST_STATE_TB ON DEBUG_TB.TEST_STATE_SEQ = TEST_STATE_TB.TEST_STATE_SEQ INNER JOIN TEST_TB ON TEST_STATE_TB.TEST_SEQ = TEST_TB.TEST_SEQ INNER JOIN TEST_ITEM_TB ON TEST_ITEM_TB.TEST_ITEM_SEQ = TEST_TB.TEST_ITEM_SEQ AND TEST_ITEM_TB.STATE != 'D' INNER JOIN JOB_TB ON JOB_TB.quo_ym = TEST_ITEM_TB.quo_ym AND JOB_TB.QUO_SEQ = TEST_ITEM_TB.QUO_SEQ LEFT JOIN DEBUG_STATE_TB ON DEBUG_STATE_TB.DEBUG_STATE_SEQ = ( SELECT DEBUG_STATE_SEQ FROM DEBUG_STATE_TB sub WHERE sub.DEBUG_SEQ = DEBUG_TB.DEBUG_SEQ order by DEBUG_STATE_SEQ desc limit 1 ) WHERE 1=1
and DATE_FORMAT(DEBUG_TB.INS_DT, '%Y%m%d') between date_format(#{item.startDate}, '%Y%m%d') and date_format(#{item.endDate}, '%Y%m%d')
and DATE_FORMAT(DEBUG_TB.UDT_DT, '%Y%m%d') between date_format(#{item.startDate}, '%Y%m%d') and date_format(#{item.endDate}, '%Y%m%d')
and DEBUG_STATE_TB.STATE_CODE = #{item.searchWord}
and DEBUG_TB.STATE_CODE = #{item.searchWord}
and CONCAT ( FN_GET_SEQSTRING ('SB', JOB_TB.sbk_ym, 'G', JOB_TB.sbk_seq), '-', TEST_TB.TEST_TYPE_CODE, LPAD (TEST_TB.TEST_NO, 4, 0) ) like concat('%',#{item.searchWord},'%')
and IFNULL (TEST_ITEM_TB.product, JOB_TB.PRDCT_NAME) like concat('%',#{item.searchWord},'%')
and TEST_ITEM_TB.model like concat('%',#{item.searchWord},'%')
order by DEBUG_TB.ins_dt ) lst order by no desc LIMIT #{pageUnit} OFFSET #{firstIndex};
